3:{s:4:\"file\";s:25:\"zoufuqiu-2011-940x198.jpg\";s:5:\"width\";s:3:\"940\";s:6:\"height\";s:3:\"198\";}}s:10:\"image_meta\";a:10:{s:8:\"aperture\";s:3:\"4.5\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:14:\"Canon EOS 500D\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:10:\"1296475670\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:2:\"34\";s:3:\"iso\";s:3:\"400\";s:13:\"shutter_speed\";s:17:\"0.016666666666667\";s:5:\"title\";s:0:\"\";}}'),(46,38,'_edit_lock','1302448426'),(71,50,'_wp_attachment_image_alt','邹富秋在日本参加书法交流'),(49,38,'_wp_old_slug',''),(45,38,'_edit_last','2'),(22,22,'_edit_lock','1297313669'),(21,22,'_edit_last','2'),(29,26,'_edit_lock','1297437970'),(25,22,'_wp_old_slug',''),(28,26,'_edit_last','2'),(32,26,'_wp_old_slug','');
